import React, { useState } from 'react'
import { Link } from 'react-router-dom'

import InputForm from '../InputForm/InputForm'
import style from"./Signup.module.css"



export default function Signup() {
    const [values,setValues] = useState({
        name:"",
        email:"",
        pass:""
    })

    const [errormsg,seterrorMsg] = useState()
    const handleSubmission=()=>{
        if(!values.name || !values.email || !values.pass){
        seterrorMsg("Fill all fields")
        return;
        }
        seterrorMsg("")
        createUserWithEmailAndPassword(auth,values.email,values.pass).then((res)=>{
            const user = res.user;
            console.log(user)
            updateProfile(user,{
                displayName:values.name
            })
        })
       
    }
  return (
    <div className={style.container}>
        <div className={style.innerBox}>
            <h1 className={style.heading}>Signup</h1>
            <InputForm label="Name :" type="text" placeholder="Enter your Name" 
            onChange={(event)=>{setValues((prev)=>({...prev,name:event.target.value}))}} />
            <InputForm label="Email" type="email" placeholder="example@abc"
            onChange={(event)=>{setValues((prev)=>({...prev,email:event.target.value}))}}/>
            <InputForm label="Password" type="password" placeholder="Enter your password"
            onChange={(event)=>{setValues((prev)=>({...prev,pass:event.target.value}))}}/>
            <div className={style.footer}>
                <b className={style.error}>{errormsg}</b>
                <button onClick={handleSubmission}>SignUp</button>
                <p>Already have an account? </p>
                <span>
                <Link to='/Login'>Login</Link>
                </span>
            </div>
        </div>

    </div>
  )
}
